import React, { Component } from "react";
import NavBar from "./components/NavBar";
import NewFeed from "./components/newfeed/NewFeed";
import Carts from "./components/carts/Carts";

class App extends Component {
  constructor() {
    super();
    state = {
      atNewFeed: true,
      items: [
        {
          name: "Iphone XS Max 512GB Gold",
          id: 1,
          src: "https://img.mvideo.ru/Pdb/30040031b.jpg",
          price: "116,990 rub",
          added: false,
          button: "secondary",
          message: "Add to cart"
        },
        {
          name: "Samsung S10+",
          id: 2,
          src: "https://img.mvideo.ru/Pdb/30042519b.jpg",
          price: "124,990 rub",
          added: false,
          button: "secondary",
          message: "Add to cart"
        },
        {
          name: "Macbook pro 15",
          id: 3,
          src: "https://img.mvideo.ru/Pdb/30039192b.jpg",
          price: "528,990 rub",
          added: false,
          button: "secondary",
          message: "Add to cart"
        },
        {
          name: "MSI GT75VR 7RE-054RU Titan SLI",
          id: 4,
          src: "https://img.mvideo.ru/Pdb/30031607b.jpg",
          price: "246,990 rub",
          added: false,
          button: "secondary",
          message: "Add to cart"
        }
      ]
    };

    this.handleClickAdd = this.handleClickAdd.bind(this);
  }

  handleClickNewFeed = () => {
    this.setState({
      atNewFeed: true
    });
  };

  handleClickCart = () => {
    this.setState({
      atNewFeed: false
    });
  };

  handleClickAdd(item) {
    const items = [...this.state.items];
    const index = items.indexOf(item);
    items[index] = { ...item };
    items[index].added = !items[index].added;
    items[index].button = items[index].added === true ? "success" : "secondary";
    items[index].message =
      items[index].added === true ? "Added" : "Add to cart";
    this.setState({ items });
  };

  handleClickRemove = () => {};

  render() {
    return (
      <React.Fragment>
        <NavBar
          onClickNewFeed={this.handleClickNewFeed}
          onClickCart={this.handleClickCart}
        />
        {this.state.atNewFeed && (
          <NewFeed items={this.state.items} onClickAdd={this.handleClickAdd} />
        )}
        {!this.state.atNewFeed && (
          <Carts
            item={this.state.items}
            onClickRemove={this.handleClickRemove}
          />
        )}
      </React.Fragment>
    );
  }
}

export default App;
