import React, { Component } from "react";

class Item extends Component {
  state = {
    added: false
  };

  handleClick() {
    this.setState({
      added: !this.state.added
    });
  }

  render() {
    const { name, src, price } = this.props.item;

    return (
      <React.Fragment>
        <p align="center">
          <img src={src} alt={name} />
          <h3>{name}</h3>
          <h3>Price: {price}</h3>
          <button
            onClick={this.handleClick}
            className="btn btn-secondary btn-sm"
          >
            Add to cart
          </button>
        </p>
      </React.Fragment>
    );
  }
}

export default Item;
