import React, { Component } from "react";
import Counters from "./counters/Counters";

class Cart extends Component {
  render() {
    const {
      onClickIncrement,
      onClickDecrement,
      onClickReset,
      onClickRemove
    } = this.props;

    const items = [...this.props.items.filter(item => item.added)];

    return (
      <React.Fragment>
        <Counters
          items={items}
          onClickIncrement={this.props.onClickIncrement}
          onClickDecrement={this.props.onClickDecrement}
          onClickReset={this.props.onClickReset}
          onClickRemove={this.props.onClickRemove}
        />
      </React.Fragment>
    );
  }
}

export default Cart;
