import React, { Component } from "react";
import Items from "./items/Items";

class NewFeed extends Component {
  state = {
    items: [
      {
        name: "Iphone XS Max 512GB Gold",
        id: 1,
        src: "https://img.mvideo.ru/Pdb/30040031b.jpg",
        price: "116,990 rub",
        added: false,
        button: "secondary",
        message: "Add to cart"
      },
      {
        name: "Samsung S10+",
        id: 2,
        src: "https://img.mvideo.ru/Pdb/30042519b.jpg",
        price: "124,990 rub",
        added: false,
        button: "secondary",
        message: "Add to cart"
      },
      {
        name: "Macbook pro 15",
        id: 3,
        src: "https://img.mvideo.ru/Pdb/30039192b.jpg",
        price: "528,990 rub",
        added: false,
        button: "secondary",
        message: "Add to cart"
      },
      {
        name: "MSI GT75VR 7RE-054RU Titan SLI",
        id: 4,
        src: "https://img.mvideo.ru/Pdb/30031607b.jpg",
        price: "246,990 rub",
        added: false,
        button: "secondary",
        message: "Add to cart"
      }
    ]
  };

  handleClickAdd = item => {
    const items = [...this.state.items];
    const index = items.indexOf(item);
    items[index] = { ...item };
    items[index].added = !items[index].added;
    this.setState({ items });
  };

  render() {
    return <Items items={this.state.items} onClickAdd={this.handleClickAdd} />;
  }
}

export default NewFeed;
