import React, { Component } from "react";

class Item extends Component {
  state = {
    added: false,
    button: ""
  };

  handleClick = () => {
    this.setState({
      added: !this.state.added
    });
    this.setState({
      button: this.state.added === true ? "success" : "secondary"
    });
  };

  render() {
    const { name, src, price } = this.props.item;

    return (
      <React.Fragment>
        <p align="center">
          <img src={src} alt={name} />
          <h3>{name}</h3>
          <h3>Price: {price}</h3>
          <button
            onClick={this.handleClick}
            className={"btn btn-sm btn-" + this.state.button}
          >
            Add to cart
          </button>
        </p>
      </React.Fragment>
    );
  }
}

export default Item;
