import React, { Component } from "react";
import ReactDOM from "react-dom";
import App from "./App";
import NavBar from "./NavBar";
import Carts from "./carts/Carts";
import * as serviceWorker from "./serviceWorker";
import "bootstrap/dist/css/bootstrap.css";

class Index extends Component {
  state = {
    atNewFeed: true,
    items: [
      {
        name: "Iphone XS Max 512GB Gold",
        id: 1,
        src: "https://img.mvideo.ru/Pdb/30040031b.jpg",
        price: "116,990 rub"
      },
      {
        name: "Samsung S10+",
        id: 2,
        src: "https://img.mvideo.ru/Pdb/30042519b.jpg",
        price: "124,990 rub"
      },
      {
        name: "Macbook pro 15",
        id: 3,
        src: "https://img.mvideo.ru/Pdb/30039192b.jpg",
        price: "528,990 rub"
      },
      {
        name: "MSI GT75VR 7RE-054RU Titan SLI",
        id: 4,
        src: "https://img.mvideo.ru/Pdb/30031607b.jpg",
        price: "246,990 rub"
      }
    ]
  };

  handleClick = () => {
    this.setState({
      atNewFeed: true
    });
  };

  handleClickCart = () => {
    this.setState({
      atNewFeed: false
    });
  };

  render() {
    return (
      <React.Fragment>
        <NavBar
          onClickNewFeed={this.handleClickNewFeed}
          onClickCart={this.handleClickCart}
        />
        {this.state.atNewFeed && <App />}
        {!this.state.atNewFeed && <Carts />}
      </React.Fragment>
    );
  }
}

export default Index;

ReactDOM.render(<Index />, document.getElementById("root"));

serviceWorker.unregister();
